import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';

//import MyGreatPlace from './my_great_place';

class Tester extends Component {
  constructor(props) {
    super(props)
    this.state = {
      center: {
        lat: 32.7096422,    //default downtown San Diego
        lng: -117.1579546
      },
      zoom: 14,
      address: ''
    }
  }

  handleChange = (e) => {
    this.setState({
      address:  e.target.value
    })
  }

  handleSearch = (e) => {
    const url = 'https://maps.googleapis.com/maps/api/geocode/json'
    const apiKey = 'AIzaSyCYw5lzQYX9SL8qlqqKZ5latLcBuRYfDyQ'
    e.preventDefault()
    console.log(this.state.address)
    fetch(url + '?address=' + this.state.address + '&key=' + apiKey)
    .then(resp => {
      let json = resp.json()
      return json
    })
    .then(json => {
      console.log(json)
      console.log(json.results[0].geometry.location) // <-- to get obj containing lat/lng
      this.setState({
        center: {
          lat: json.results[0].geometry.location.lat,
          lng: json.results[0].geometry.location.lng
        }
      })
    })
    .catch(err => {
      console.log(err)
    })
  }

  determinePointers = () => {
    const K_WIDTH = 40;
    const K_HEIGHT = 40;

    const pointerStyle = {
      // initially any map object has left top corner at lat lng coordinates
      // it's on you to set object origin to 0,0 coordinates
      position: 'absolute',
      width: K_WIDTH,
      height: K_HEIGHT,
      left: -K_WIDTH / 2,
      top: -K_HEIGHT / 2,

      border: '5px solid #f44336',
      borderRadius: K_HEIGHT,
      backgroundColor: 'white',
      textAlign: 'center',
      color: '#3f51b5',
      fontSize: 16,
      fontWeight: 'bold',
      padding: 4
    }
    return (
       <div style={pointerStyle}>

       </div>
    )
  }

  render() {
    return (
      <div className="page">
        LAT: {this.state.center.lat}, LNG: {this.state.center.lng}
        <div style={{ height: '75vh', width: '70%', margin: '0 auto' }}>
          <GoogleMapReact
            bootstrapURLKeys={{ key: 'AIzaSyCYw5lzQYX9SL8qlqqKZ5latLcBuRYfDyQ' }}
            center={this.state.center}
            zoom={this.state.zoom}>
            {this.determinePointers()}
            // <MyGreatPlace lat={32.7096422} lng={-117.1579546} text={'A'} />
            // <MyGreatPlace lat={32.7565162} lng={-117.1122965} text={'B'} />
          </GoogleMapReact>
        </div>
        <br/>
        <form>
          <input type="text" name="address" placeholder="Enter Address" onChange={this.handleChange} />
          <input type="submit" value="Submit" onClick={this.handleSearch} />
        </form>
      </div>
    );
  }
}

export default Tester;
